import React, { useState, useEffect } from 'react';
import Banner from '../components/Banner';
import AnimeRow from '../components/AnimeRow';
import { fetchTopAnime, fetchAnimeByGenre } from '../../utils/api';

const Home = () => {
  const [topAnime, setTopAnime] = useState([]);
  const [shounenAnime, setShounenAnime] = useState([]);
  const [sliceOfLifeAnime, setSliceOfLifeAnime] = useState([]);
  const [featuredAnime, setFeaturedAnime] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const top = await fetchTopAnime();
      setTopAnime(top);
      setFeaturedAnime(top[0]);

      const shounen = await fetchAnimeByGenre(27); // 27 is Shounen
      setShounenAnime(shounen);

      const sliceOfLife = await fetchAnimeByGenre(36); // 36 is Slice of Life
      setSliceOfLifeAnime(sliceOfLife);
    };

    fetchData();
  }, []);

  return (
    <div>
      <Banner anime={featuredAnime} />
      <main className="container mx-auto px-4 pt-20">
        <AnimeRow title="Top Anime" animes={topAnime} />
        <AnimeRow title="Popular Shounen" animes={shounenAnime} />
        <AnimeRow title="Slice of Life" animes={sliceOfLifeAnime} />
      </main>
    </div>
  );
};

export default Home;
