import React from 'react';
import { Link } from 'react-router-dom';
import { Search, Bell, User } from 'lucide-react';

const Header = () => (
  <header className="bg-purple-900 text-white p-4 fixed top-0 w-full z-50">
    <div className="container mx-auto flex justify-between items-center">
      <Link to="/" className="text-2xl font-bold text-pink-400">AnimeFlix</Link>
      <nav className="hidden md:flex space-x-4">
        <Link to="/" className="hover:text-pink-300">Home</Link>
        <Link to="/search" className="hover:text-pink-300">Search</Link>
        <a href="#" className="hover:text-pink-300">My List</a>
      </nav>
      <div className="flex items-center space-x-4">
        <Link to="/search"><Search className="cursor-pointer hover:text-pink-300" /></Link>
        <Bell className="cursor-pointer hover:text-pink-300" />
        <User className="cursor-pointer hover:text-pink-300" />
      </div>
    </div>
  </header>
);

export default Header;
